div.script:

 $(document).ready(function() {

    var availableDates = [];


            availableDates.push({
                attribute_id: '',
                value_id: '',
                date: '' 
            });


    var enabledDates = availableDates.map(function(item) {
        return { 
            date: new Date(item.date),
            attribute_id: item.attribute_id,
            value_id: item.value_id
        };
    });


    flatpickr("#date-picker", {
        enable: enabledDates.map(function(item) {
            return item.date;
        }),
        disable: [
            function(date) {

                return !enabledDates.some(function(enabledDate) {
                    return enabledDate.date.toDateString() === date.toDateString();
                });
            }
        ],
        onReady: function (selectedDates, dateStr, instance) {
            // Add the Clear button to the calendar popup
            var calendarContainer = instance.calendarContainer;
            var clearButton = document.createElement('button');
            clearButton.classList.add('flatpickr-clear-button');
            clearButton.innerHTML = 'Clear';

            // Append the button inside the calendar
            calendarContainer.appendChild(clearButton);

            // Add click event to clear the selected date
            clearButton.addEventListener('click', function() {
                // Clear the input field and hidden value
                $('#date-picker').val('');
                $('#selected-date-id').val('');

                // Close the calendar
                instance.close();
            });
        },
        onChange: function(selectedDates, dateStr, instance) {

            var selectedDate = new Date(dateStr);
            var selectedItem = enabledDates.find(function(item) {
                return item.date.toDateString() === selectedDate.toDateString();
            });

            if (selectedItem) {

                $('#selected-date-id').val(selectedItem.attribute_id + '-' + selectedItem.value_id);
            }
        }
    });



});
